sys_variable_value.value:
(function execute(inputs, outputs) {

  // Find associated field definitions

  var findRec = new GlideRecord(inputs.target_table);
  findRec.get(inputs.target_sys_id);
  var getFields = new GlideRecord('x_9104_pdf_manager_pdf_field_matching');
  getFields.addQuery('pdf_creation_definition',inputs.pdf_creation_def.getValue('sys_id'));
  getFields.addQuery('pdf_field.pdf_document',inputs.pdf_document.getValue('sys_id'));
  getFields.addQuery('active',true);
  getFields.query();

  var dataObj = {};

  // Go through each field

  while (getFields.next()) {
    var outputValue = '';
    var propertyName = getFields.pdf_field.name.toString();

    // If fields are using the field name from the target record
    if (getFields.getValue('type') == 'field') {
      outputValue = findRec.getElement(getFields.getValue('field')).toString();
    }
    // End if when using just field name

    // If field is using a script
    if (getFields.getValue('type') == 'script') {
      var vars = { 
            'answer' : '',
             'record':findRec
      }
      var evaluator = new GlideScopedEvaluator;
      evaluator.evaluateScript(getFields , 'script', vars);
      outputValue = evaluator.getVariable('answer').toString();
    }

    if (getFields.getValue('type') == 'today_date') {
       var gcdt = new GlideDateTime();
      outputValue = gcdt.getDate().toString();

    }

    // End If for using script

    // If field is of type radio_button, find associated output value
    if (getFields.pdf_field_type == 'radio_button') {
         var findChoice = new GlideRecord('x_9104_pdf_manager_pdf_field_matching_choice');
         findChoice.addQuery('record_value',outputValue);
    	 findChoice.addQuery('pdf_field_matching',getFields.getValue('sys_id'));
         findChoice.query();
         if (findChoice.next()) {
            outputValue = findChoice.getValue('pdf_value').toString(); 
         }           
       }
    // End if of finding output value for radio button
     dataObj[propertyName] = outputValue;
  }

  var pdf = new sn_pdfgeneratorutils.PDFGenerationAPI;

  var outputObj = pdf.fillDocumentFields(dataObj, inputs.attachment_sys_id, inputs.target_table, inputs.target_sys_id, inputs.output_pdf_name);
  outputs.status = outputObj.status;
  outputs.output_sys_id = outputObj.attachment_id;
  outputs.message = outputObj.message;

})(inputs, outputs);

